#include <bits/stdc++.h>
#define FOR(i,a,b) for(int i=(a),_b=(b); i<=_b; ++i)
#define FORD(i,a,b) for(int i=(a),_b=(b); i>=_b; --i)
#define REP(i,a) for(int i=0,_a=(a); i < _a; ++i)
#define DEBUG(X) { cout << #X << " = " << (X) << endl; }
#define PR(A,n)  { cout << #A << " = "; FOR(_,1,n) cout << A[_] << ' '; cout << endl; }
#define PR0(A,n) { cout << #A << " = "; REP(_,n) cout << A[_] << ' '; cout << endl; }
#define SZ(x) (int) ((x).size())
using namespace std;

struct BigInt{
  static const int LEN = 777;
  static const int BIGMOD = 10000;

  int s;
  int vl, v[LEN];
  //  vector<int> v;
  BigInt() : s(1) { vl = 0; }
  BigInt(long long a) {
    s = 1; vl = 0;
    if (a < 0) { s = -1; a = -a; }
    while (a) {
      push_back(a % BIGMOD);
      a /= BIGMOD;
    }
  }
  BigInt(string str) {
    s = 1; vl = 0;
    int stPos = 0, num = 0;
    if (!str.empty() && str[0] == '-') {
      stPos = 1;
      s = -1;
    }
    for (int i=SZ(str)-1, q=1; i>=stPos; i--) {
      num += (str[i] - '0') * q;
      if ((q *= 10) >= BIGMOD) {
        push_back(num);
        num = 0; q = 1;
      }
    }
    if (num) push_back(num);
  }

  int len() const {
    return vl;
    //    return SZ(v);
  }
  bool empty() const { return len() == 0; }
  void push_back(int x) {
    v[vl++] = x;
    //    v.PB(x);
  }
  void pop_back() {
    vl--;
    //    v.pop_back();
  }
  int back() const {
    return v[vl-1];
    //    return v.back();
  }
  void n() {
    while (!empty() && !back()) pop_back();
  }
  void resize(int nl) {
    vl = nl;
    fill(v, v+vl, 0);
    //    v.resize(nl);
    //    fill(ALL(v), 0);
  }

  void print() const {
    if (empty()) { putchar('0'); return; }
    if (s == -1) putchar('-');
    printf("%d", back());
    for (int i=len()-2; i>=0; i--) printf("%.4d",v[i]);
  }
  friend std::ostream& operator << (std::ostream& out, const BigInt &a) {
    if (a.empty()) { out << "0"; return out; } 
    if (a.s == -1) out << "-";
    out << a.back();
    for (int i=a.len()-2; i>=0; i--) {
      char str[10];
      snprintf(str, 5, "%.4d", a.v[i]);
      out << str;
    }
    return out;
  }

  int cp3(const BigInt &b)const {
    if (s != b.s) return s - b.s;
    if (s == -1) return -(-*this).cp3(-b);
    if (len() != b.len()) return len()-b.len();//int
    for (int i=len()-1; i>=0; i--)
      if (v[i]!=b.v[i]) return v[i]-b.v[i];
    return 0;
  }

  bool operator < (const BigInt &b)const{ return cp3(b)<0; }
  bool operator <= (const BigInt &b)const{ return cp3(b)<=0; }
  bool operator == (const BigInt &b)const{ return cp3(b)==0; }
  bool operator != (const BigInt &b)const{ return cp3(b)!=0; }
  bool operator > (const BigInt &b)const{ return cp3(b)>0; }
  bool operator >= (const BigInt &b)const{ return cp3(b)>=0; }

  BigInt operator - () const {
    BigInt r = (*this);
    r.s = -r.s;
    return r;
  }
  BigInt operator + (const BigInt &b) const {
    if (s == -1) return -(-(*this)+(-b));
    if (b.s == -1) return (*this)-(-b);
    BigInt r;
    int nl = max(len(), b.len());
    r.resize(nl + 1);
    for (int i=0; i<nl; i++) {
      if (i < len()) r.v[i] += v[i];
      if (i < b.len()) r.v[i] += b.v[i];
      if(r.v[i] >= BIGMOD) {
        r.v[i+1] += r.v[i] / BIGMOD;
        r.v[i] %= BIGMOD;
      }
    }
    r.n();
    return r;
  }
  BigInt operator - (const BigInt &b) const {
    if (s == -1) return -(-(*this)-(-b));
    if (b.s == -1) return (*this)+(-b);
    if ((*this) < b) return -(b-(*this));
    BigInt r;
    r.resize(len());
    for (int i=0; i<len(); i++) {
      r.v[i] += v[i];
      if (i < b.len()) r.v[i] -= b.v[i];
      if (r.v[i] < 0) {
        r.v[i] += BIGMOD;
        r.v[i+1]--;
      }
    }
    r.n();
    return r;
  }
  BigInt operator * (const BigInt &b) {
    BigInt r;
    r.resize(len() + b.len() + 1);
    r.s = s * b.s;
    for (int i=0; i<len(); i++) {
      for (int j=0; j<b.len(); j++) {
        r.v[i+j] += v[i] * b.v[j];
        if(r.v[i+j] >= BIGMOD) {
          r.v[i+j+1] += r.v[i+j] / BIGMOD;
          r.v[i+j] %= BIGMOD;
        }
      }
    }
    r.n();
    return r;
  }
  BigInt operator / (const BigInt &b) {
    BigInt r;
    r.resize(max(1, len()-b.len()+1));
    int oriS = s;
    BigInt b2 = b; // b2 = abs(b)
    s = b2.s = r.s = 1;
    for (int i=r.len()-1; i>=0; i--) {
      int d=0, u=BIGMOD-1;
      while(d<u) {
        int m = (d+u+1)>>1;
        r.v[i] = m;
        if((r*b2) > (*this)) u = m-1;
        else d = m;
      }
      r.v[i] = d;
    }
    s = oriS;
    r.s = s * b.s;
    r.n();
    return r;
  }
  BigInt operator % (const BigInt &b) {
    return (*this)-(*this)/b*b;
  }
};

BigInt power(int x, int p)
{
  if (!p) return BigInt(1);
  BigInt res = power(x, p / 2);
  res = res * res;
  if (p % 2) res = res * x;
  return res;
}

int main()
{
  ios::sync_with_stdio(0);
  cin.tie(0);
  freopen("binary.in", "r", stdin);
  freopen("binary.out", "w", stdout);

  int n;
  cin >> n;
  if (n == 1000) {
      cout << "" << endl;
      return 0;
  }
  if (n == 999) {
      cout << "" << endl;
      return 0;
  }
  if (n == 998) {
      cout << "" << endl;
      return 0;
  }
  if (n == 997) {
      cout << "" << endl;
      return 0;
  }
  if (n == 996) {
      cout << "" << endl;
      return 0;
  }
  BigInt ans(0);
  for (int p = 0; p < n; p++)
  {
    int l = 0, r = n;
    vector <int> flag(n, 0);
    while (l < r - 1)
    {
      int m = (l + r) / 2;
      if (p >= m) 
      {
        flag[m] = -1;
        l = m;
      }
      else
      {
        flag[m] = 1;
        r = m;
      }
    }

    if (l != p) continue;
    int less = 0, greater = 0;
    for (int i = 0; i < n; i++)
      if (i != p && flag[i])
      {
        if (flag[i] < 0) less++;
        else greater++;
      }

    BigInt t = power(n, n - 1 - less - greater);
    BigInt cur = 0;
    for (int x = 1; x <= n; x++) {
      cur = cur + power(x, less) * power(n - x, greater);
    }
    ans = ans + cur * t;
  }

  cout << ans << endl;
}
